INSERT INTO `cache_filter` VALUES ('1:f17aafbcc73c61098714548b0972a05e','<p>Inuits, the leading Linux and Open Source consultancy group is launching it\'s new website.</p>\n',1242216105,1242129705,'',0),('1:ae2f9401ffb61dae8e044ad35f553bb0','<p>The Inuits  will present 3   talks at the upcoming T-Dose conference in Eindhoven </p>\n<p>On saturday Kris Buytaert will  present in the Drupal room about his  Drupal and MySQL Cluster Experiences.</p>\n',1242216105,1242129705,'',0),('1:7179b96e1969a59cf4984f2f0b5e66f8','<p>Inuits will be presenting about Open Source Virtualization at OpenExpo in Zurich and OpenSourceDays  in Copenhagen.</p>\n',1242216105,1242129705,'',0),('1:5a34deafa0520c9c3d1205b844674501','<p>WHY BLOCK</p>\n',1242216105,1242129705,'',0),('1:7de8987dc493ba5af87044ba761cc1a8','<p>WHO BLOCK</p>\n',1242216105,1242129705,'',0),('1:90d87be3d0f0596b44cc84ae8000013f','<p>WHERE BLOCK</p>\n',1242216105,1242129705,'',0),('1:e5a7139c3b55d96fbaaaf0de3afaeb50','<p>Meme from <a href=\"http://planet.ubuntu.com\">planet ubuntu</a></p>\n<p>1. Grab the nearest book.<br />\n2. Open it to page 56.<br />\n3. Find the fifth sentence.<br />\n4. Post the text of the sentence in your journal along with these instructions.<br />\n5. Don’t dig for your favorite book, the cool book, or the intellectual one: pick the CLOSEST.</p>\n<p>\"Altough named selection sets let you quickly select individual objects, grouping objects together forces all the members of a group to act like a single entity.\"</p>\n<p>3ds Max 4 In Depth (Jon McFarland, Rob Polevoi)</p>\n',1242216105,1242129705,'',0),('2:357f400a271caaf568505bd3bcb8b9eb','<p><a href=\"www.vlaanderen.be\"><img scr=\"http://www.vlaanderen.be/servlet/Satellite?blobcol=urldata&amp;blobheader=image%2Fgif&amp;blobkey=id&amp;blobtable=Media_C_Blobs&amp;blobwhere=1186549318995&amp;cachecontrol=never&amp;ssbinary=true\" /></a></p>\n',1242216105,1242129705,'',0),('2:a190bd0a814ee7477035c9a0a262990a','<p><img scr=\"http://www.eds.be/nlapps/data/docattachments/nieuw%20logo%20VO.jpg\" /><img /></p>\n',1242216485,1242130085,'',0);
